import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import FormContainer from '../components/FormContainer'
import { listProductDetails, createProduct } from '../actions/productActions'
import { PRODUCT_CREATE_RESET } from '../constants/productConstants'
import { v4 as uuid } from 'uuid';

const ProductScreen = ({ match, history }) => {
	// const productId = match.params.id
	const productId = uuid();
	const [name, setName] = useState('')
	const [price, setPrice] = useState(0)
	const [image, setImage] = useState('')
	const [brand, setBrand] = useState('')
	const [countInStock, setCountInStock] = useState(0)
	const [category, setCategory] = useState('')
	const [description, setDescription] = useState('')
	const [uploading, setUploading] = useState(false)

	const dispatch = useDispatch()

	// useSelector is to grab what we want from the state
	const productDetails = useSelector((state) => state.productDetails)
	const { loading, error, product } = productDetails

	const productCreate = useSelector((state) => state.productCreate)
	const {
		loading: loadingUpdate,
		error: errorUpdate,
		success: successCreate,
	} = productCreate

	// make request here upon component load
	useEffect(() => {
		if (successCreate) {
			// dispatch({ type: PRODUCT_CREATE_RESET })
			// dispatch(listProductDetails(productId))
			history.push('/admin/productlist')
		} 
		//else {
		// 	if (!product.name || product._id !== productId) {
		// 		dispatch(listProductDetails(productId))
		// 	} else {
		// 		setName(product.name)
		// 		setPrice(product.price)
		// 		setImage(product.image)
		// 		setBrand(product.brand)
		// 		setCountInStock(product.countInStock)
		// 		setCategory(product.category)
		// 		setDescription(product.description)
		// 	}
		// }


	}, [successCreate, dispatch, history, product, productId]) // Dependencies, on change they fire off useEffect)

	const uploadFileHandler = async (e) => {
		const file = e.target.files[0]

		const formData = new FormData()
		formData.append('image', file)
		setUploading(true)
		try {
			const config = {
				headers: {
					'Content-Type': 'multipart/form-data',
				},
			}

			// Making post request to upload the image
			const { data } = await axios.post('/api/upload', formData, config)

			setImage(data)
			setUploading(false)
		} catch (error) {
			console.error(error)
			setUploading(false)
		}
	}

	const submitHandler = (e) => {
		e.preventDefault()
		dispatch(
			createProduct({
				_id: productId,
				name,
				price,
				image,
				brand,
				countInStock,
				category,
				description,
			})
			)
	}

	return (
		<>
			<Link to='/admin/productlist' className='btn btn-light my-3'>
				Go Back
			</Link>
			<FormContainer>
				<h1>Create Product</h1>
				{loadingUpdate && <Loader />}
				{errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}
				{/* On error, display message/error
            When loading, display Loading... */}
				{loading ? (
					<Loader />
				) : error ? (
					<Message variant='danger'>{error}</Message>
				) : (
					<Form onSubmit={submitHandler}>
						{/* Name */}
						<Form.Group controlId='email'>
							<Form.Label>Name</Form.Label>
							<Form.Control
								type='text'
								placeholder='Enter name'
								value={name}
								onChange={(e) => setName(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Price */}
						<Form.Group controlId='price'>
							<Form.Label>Price</Form.Label>
							<Form.Control
								type='number'
								min='0'
								step='0.01'
								placeholder='Enter price'ProductDetails
								value={price}
								onChange={(e) => setPrice(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Image */}
						<Form.Group controlId='image'>
							<Form.Label>Image</Form.Label>
							<Form.Control
								type='text'
								placeholder='Enter image url'
								value={image}
								onChange={(e) => setImage(e.target.value)}
							></Form.Control>
							{/* Image file */}
							<Form.File
								id='image-file'
								label='Choose File'
								custom
								onChange={uploadFileHandler}
							></Form.File>
							{uploading && <Loader />}
						</Form.Group>
						{/* Brand */}
						<Form.Group controlId='brand'>
							<Form.Label>Brand</Form.Label>
							<Form.Control
								type='text'
								placeholder='Enter brand'
								value={brand}
								onChange={(e) => setBrand(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Count in Stock */}
						<Form.Group controlId='countInStock'>
							<Form.Label>Count In Stock</Form.Label>
							<Form.Control
								type='number'
								min='0'
								step='1'
								placeholder='Enter count in stock'
								value={countInStock}
								onChange={(e) => setCountInStock(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Category */}
						<Form.Group controlId='category'>
							<Form.Label>Category</Form.Label>
							<Form.Control
								type='text'
								placeholder='Enter category'
								value={category}
								onChange={(e) => setCategory(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Description */}
						<Form.Group controlId='description'>
							<Form.Label>Description</Form.Label>
							<Form.Control
								type='text'
								placeholder='Enter description'
								value={description}
								onChange={(e) => setDescription(e.target.value)}
							></Form.Control>
						</Form.Group>
						{/* Button */}
						<Button type='submit' variant='primary'>
							Add Product
						</Button>
					</Form>
				)}
			</FormContainer>
		</>
	)
}

export default ProductScreen
